/**
 * Phaser game configuration object
 * @type {Phaser.Types.Core.GameConfig}
 * @property {number} width Canvas width (800px)
 * @property {number} height Canvas height (600px)
 * @property {Phaser.AUTO} type Auto-select best renderer
 * @property {object} physics Arcade physics settings
 * @property {string} physics.default Arcade physics engine
 * @property {object} physics.arcade Gravity and collision config
 * @property {boolean} physics.arcade.debug Visual debug mode
 * @property {object} scene Game state handlers
 * @description Configures core game engine settings including canvas size, physics system, and scene management
 */
// Core game scenes
import { GameOver } from '../scenes/menus/GameOver';
import { WayneWorld } from '../scenes/levels/WayneWorld';

// Menu scenes
import { MainMenu } from '../scenes/menus/MainMenu';
import { Leaderboard } from '../scenes/menus/Leaderboard';
import { MissionComplete } from '../scenes/menus/MissionComplete';
import { DevHub } from '../scenes/menus/DevHub/DevHub';
import { Credits } from '../scenes/menus/Credits';

// Settings scenes
import Settings from '../scenes/menus/Settings';
import ControlsSettings from '../scenes/menus/ControlsSettings';
import SoundSettings from '../scenes/menus/SoundSettings';
import { SoundTester } from '../scenes/menus/DevHub/SoundTester';

// Core engine scenes
import { Boot } from '../scenes/base/Boot';
import { Preloader } from '../scenes/base/Preloader';
import { PauseMenu } from '../scenes/menus/PauseMenu';

/**
 * @typedef {Object} GameConfig
 * @property {string|number} type - Rendering type (Phaser.AUTO, Phaser.CANVAS, or numeric)
 * @property {boolean} pixelArt - Whether to optimize for pixel art graphics
 * @property {Object} scale - Display scaling settings
 * @property {Object} dom - DOM element support settings
 * @property {Object} physics - Physics engine configuration
 * @property {Object} input - Input handling settings
 * @property {Array} scene - List of scenes to load in order
 */

/**
 * GAME CONFIGURATION
 * Core settings that define how the game engine behaves
 * @type {GameConfig}
 */
export const config = {
    // Rendering configuration
    type: Phaser.AUTO,          // Let Phaser choose WebGL or Canvas
    pixelArt: true,             // Optimize for pixel art graphics

    // Display settings
    scale: {
        mode: Phaser.Scale.NONE,
        parent: 'game-container',    // DOM element to mount game
        width: 1920,                 // Base game resolution
        height: 1080,
        autoRound: true,             // Round pixel values
        zoom: 1,
        autoCenter: Phaser.Scale.CENTER_BOTH,  // Center game in container
        fullscreenTarget: 'game-container',
        expandParent: true
    },

    // DOM element support (for UI overlays)
    dom: {
        createContainer: true
    },

    // Physics engine settings
    physics: {
        default: 'arcade',
        arcade: {
            debug: false,           // Show physics bodies when true
            gravity: { y: 400 },    // Vertical gravity strength
            tileBias: 32            // Tile collision accuracy
        }
    },

    // Input handling
    input: {
        mouse: {
            preventDefaultWheel: true,           // Prevent page scroll on wheel
            preventDefaultContextMenu: true      // Disable right-click menu
        }
    },

    // Scene sequence (loaded in order)
    scene: [
        Boot,            // Initial setup and system initialization
        Preloader,       // Asset loading
        MainMenu,        // Game start menu
        Settings,        // General settings
        ControlsSettings,
        SoundSettings,
        WayneWorld,
        Leaderboard,
        MissionComplete,
        GameOver,
        PauseMenu,
        DevHub,
        Credits,
        SoundTester
    ]
};
