/**
 * Initialize an empty service container
 * Uses Map for efficient key-value storage of services
 * 
 * @constructor
 * @description
 * Creates a new container instance with:
 * - Empty service registry
 * - Efficient Map-based storage
 * - O(1) service lookup
 * 
 * @returns {void}
 */
constructor() {
    this.services = new Map();
}

/**
 * Register a new service in the container
 * @param {string} key - Unique identifier for the service
 * @param {Object} service - Service instance to store
 * 
 * @description
 * Registers a service instance with the container:
 * - Associates service with a unique key
 * - Overwrites existing service if key exists
 * - Supports any type of service object
 * - Enforces strict type checking on inputs
 * 
 * @example
 * container.register('events', new EventManager());
 * container.register('audio', audioManager);
 * 
 * @returns {void}
 * @throws {TypeError} If key is not a string
 * @throws {TypeError} If service is null or undefined
 */
register(key, service) {
    if (typeof key !== 'string') {
        throw new TypeError('Service key must be a string');
    }
    if (service === null || service === undefined) {
        throw new TypeError('Service instance cannot be null or undefined');
    }
    this.services.set(key, service);
}

/**
 * Retrieve a service from the container
 * @param {string} key - Identifier of the service to retrieve
 * @returns {Object} The requested service instance
 * 
 * @description
 * Retrieves a previously registered service:
 * - Looks up service by unique key
 * - Returns the service instance if found
 * - Throws error if service not registered
 * - Enforces strict type checking on key
 * 
 * @example
 * const events = container.get('events');
 * const audio = container.get('audio');
 * 
 * @throws {TypeError} If key is not a string
 * @throws {Error} If service is not found
 */
get(key) {
    if (typeof key !== 'string') {
        throw new TypeError('Service key must be a string');
    }
    if (!this.services.has(key)) {
        throw new Error(`Service ${key} not found in container`);
    }
    return this.services.get(key);
}
